correct:
struct Diary {
	var entries: String
	mutating func add(entry: String) {
		entries += "\(entry)"
	}
}

struct Bicycle {
	var currentGear: Int
	mutating func changeGear(to newGear: Int) {
		currentGear = newGear
		print("I'm now in gear \(currentGear).")
	}
}

struct Book {
	var totalPages: Int
	var pagesLeftToRead = 0
	mutating func read(pages: Int) {
		if pages < pagesLeftToRead {
			pagesLeftToRead -= pages
		} else {
			pagesLeftToRead = 0
			print("I'm done!")
		}
	}
}

struct BankAccount {
	var balance: Int
	mutating func donateToCharity(amount: Int) {
		balance -= amount
	}
}

struct Surgeon {
	var operationsPerformed = 0
	mutating func operate(on patient: String) {
		print("Nurse, hand me the scalpel!")
		operationsPerformed += 1
	}
}

struct Delorean {
	var speed = 0
	mutating func accelerate() {
		speed += 1
		if speed == 88 {
			travelThroughTime()
		}
	}
	func travelThroughTime() {
		print("Where we're going we don't need roads.")
	}
}

wrong:
struct Tree {
	var height: Int
	mutating func grow() {
		height *= 1.001
	}
}

struct Stapler {
	var stapleCount: Int
	func staple() {
		if stapleCount > 0 {
			stapleCount -= 1
			print("It's stapled!")
		} else {
			print("Please refill me.")
		}
	}
}

struct Singer {
	var name: String
	var bankBalance: Double
	mutating func goOnTour(venues: Int) {
		print("Come and see \(name) live on stage!")
		bankBalance += venues * 100_000
	}
}

struct MeetingRoom {
	var isBooked = true
	mutating book(for name: String) {
		if isBooked {
			print("Sorry, the meeting room is already taken.")
		} else {
			isBooked = true
			print("It's reserved for \(name).")
		}
	}
}

struct Car {
	let mileage: Int
	mutating func drive(distance: Int) {
		mileage += distance
	}
}

struct Switch {
	var isOn: Bool
	mutating func toggle {
		if isOn {
			isOn == false
		} else {
			isOn == true
		}
	}
}
