correct:
struct Game {
	var score: Int {
		didSet {
			print("Your score is now \(score).")
		}
	}
}

struct BankAccount {
	var name: String
	var isMillionnaire = false
	var balance: Int {
		didSet {
			if balance > 1_000_000 {
				isMillionnaire = true
			} else {
				isMillionnaire = false
			}
		}
	}
}

struct FishTank {
	var capacity: Int
	var fishCount: Int {
		didSet {
			if fishCount > capacity {
				print("You have too many fish!")
			}
		}
	}
}

struct App {
	var name: String
	var isOnSale: Bool {
		didSet {
			if isOnSale {
				print("Go and download my app!")
			} else {
				print("Maybe download it later.")
			}
		}
	}
}

struct Child {
	var name: String
	var age: Int {
		didSet {
			print("Happy birthday, \(name)!")
		}
	}
}

struct Person {
	var clothes: String {
		didSet {
			print("I'm changing to \(clothes)")
		}
	}
}

